ALL_POPULATIONS_SOURCE = \"""Short name;Full name;Male;Female;Injects;Sex with men;Sex with women;Sex worker;Client;FSW;Female sex workers;0;1;0;1;0;1;0;Clients;Clients of sex workers;1;0;0;0;1;0;1;MSM;Men who have sex with men;1;0;0;1;0;0;0;Transgender;Transgender individuals;0;0;0;1;0;0;0;PWID;People who inject drugs;0;0;1;0;0;0;0;Male PWID;Males who inject drugs;1;0;1;0;1;0;0;Female PWID;Females who inject drugs;0;1;1;1;0;0;0;Children;Children (2-15);0;0;0;0;0;0;0;Infants;Infants (0-2);0;0;0;0;0;0;0;Males 15-49;Other males (15-49);1;0;0;0;1;0;0;Females 15-49;Other females (15-49);0;1;0;1;0;0;0;Other males;Other males [enter age];1;0;0;0;1;0;0;Other females;Other females [enter age];0;1;0;1;0;0;0;"""fields = {0:"short_name",1:"name", 2:"male", 3:"female",4:"injects",5:"sexmen",6:"sexwomen",7:"sexworker",8:"client"}def populations():    from parameters import maybe_bool    lines = [l.strip() for l in ALL_POPULATIONS_SOURCE.split('\n')][2:-1]    split_lines = [l.split(';') for l in lines]    return [dict([(fields[key], maybe_bool(line[key]) ) for key in fields]) for line in split_lines]