ALL_POPULATIONS_SOURCE = \"""Internal name;Short name;Full name;Male;Female;Injects;Sex with men;Sex with women;Sex worker;Client;FSW;FSW;Female sex workers;0;1;0;1;0;1;0;CSW;Clients;Clients of sex workers;1;0;0;0;1;0;1;MSM;MSM;Men who have sex with men;1;0;0;1;0;0;0;TI;Transgender;Transgender individuals;0;0;0;1;0;0;0;PWID;PWID;People who inject drugs;0;0;1;0;0;0;0;MWID;Male PWID;Males who inject drugs;1;0;1;0;1;0;0;FWID;Female PWID;Females who inject drugs;0;1;1;1;0;0;0;CHILD;Children;Children (2-15);0;0;0;0;0;0;0;INF;Infants;Infants (0-2);0;0;0;0;0;0;0;OM15_49;Males 15-49;Other males (15-49);1;0;0;0;1;0;0;OF15_49;Females 15-49;Other females (15-49);0;1;0;1;0;0;0;OM;Other males;Other males [enter age];1;0;0;0;1;0;0;OF;Other females;Other females [enter age];0;1;0;1;0;0;0;"""fields = {1:"short_name",2:"name", 3:"male", 4:"female",5:"injects",6:"sexmen",7:"sexwomen",8:"sexworker",9:"client"}def populations():    from parameters import maybe_bool    lines = [l.strip() for l in ALL_POPULATIONS_SOURCE.split('\n')][2:-1]    split_lines = [l.split(';') for l in lines]    return [dict([(fields[key], maybe_bool(line[key]) ) for key in fields]) for line in split_lines]